mapper.update:
update Time
set REMAININGSEAT = REMAININGSEAT - #{num}
where TRAINNUMBER = #{trainnumber} and TRAINDATE = #{traindate} and (STATIONORDER between #{startOrder} and #{arriveOrder})

update Time
set REMAININGSEAT = REMAININGSEAT + #{num}
where TRAINNUMBER = #{trainnumber} and TRAINDATE = #{traindate} and (STATIONORDER between #{startOrder} and #{arriveOrder})
