mapper.update:
update Time
set REMAININGSEAT = REMAININGSEAT - #{num}
where TRAINNUMBER = #{trainnumber} and TRAINDATE = #{traindate} and STATIONORDER >= #{startOrder} and STATIONORDER <= #{arriveOrder}

update Time
set REMAININGSEAT = REMAININGSEAT + #{num}
where TRAINNUMBER = #{trainnumber} and TRAINDATE = #{traindate} and STATIONORDER >= #{startOrder} and STATIONORDER <= #{arriveOrder}
